import React, { useState } from 'react'
import { Container } from 'react-bootstrap'
import GamePageLoggedOutView from '../components/GamePageLoggedOutView'
import { HomePageProps } from '../components/types'
import styleUtils from '../styles/utils.module.css';



export default function GamePage({loggedInUser}:HomePageProps) {
  const [pinCodes, setPinCodes] = useState(Array(4).fill(''));

  // const { wordLength } = useParams();

  const handlePinCodeChange = ({index, event}:any) => {
    const newPinCodes = [...pinCodes];
    newPinCodes[index] = event.target.value;
    setPinCodes(newPinCodes);
  };

  return (    
    <Container >
     <h2 className={st}> Please type your guesses:</h2>

    <>
    {
      loggedInUser  
      ? <div className={styleUtils.pinCodeInput}>

      {pinCodes.map((pinCode, index) => (
         <input
          key={index}
          type="text"
          className={styleUtils.pinInput}
          maxLength={1}
          value={pinCode}
          onChange={(event) => handlePinCodeChange({index, event})}
        />
      ))}
    </div>
      : <GamePageLoggedOutView /> 
    }
    </>
    </Container>
  )
}
