import React, { useEffect, useState, } from 'react'
import { Button, Container } from 'react-bootstrap'
import { useParams } from 'react-router-dom';
import GamePageLoggedOutView from '../components/GamePageLoggedOutView'
import { HomePageProps } from '../components/types'
import styleUtils from '../styles/utils.module.css';
import Alphabet from '../components/Alphabet';
import Figure from '../components/Figure';
import Word from '../components/Word';


interface RouteParams {
  wordLength: string;
}

let isActive = true;  
  const correctLetters = [];
  const wrongLetters = [];

export default function GamePage({loggedInUser}:HomePageProps) {

  const { wordLength } = useParams() as unknown as RouteParams;
  const wordLengthVal = parseInt(wordLength);
  const [randomWord, setRandomWord] = useState([]);
  // const [pinGuesses, setPinGuesses] = useState(Array(wordLengthVal).fill('')); // for the guesses
  const [pinCodes, setPinCodes] = useState(Array(wordLengthVal).fill([])); //for the result
  const [isActive,setIsActive] = useState(true);
  const [correctLetters,setCorrectLetters]= useState([]);
  const [worngLetters, setWrongLetters] = useState([]);
  const API_URL = `https://api.datamuse.com/words?sp=${"?".repeat(wordLengthVal)}&max=1000`;

  const generateRandomWord = async () => {

    try {
      const response = await fetch(API_URL);
      if (response.ok) {
        const data = await response.json();
        const randomIndex = Math.floor(Math.random() * data.length);
        const randomWord = data[randomIndex].word; // Access the word property of the randomly selected index
        setRandomWord(randomWord);
        console.log(randomWord);

      } else {
        throw new Error('Failed to fetch random word');
      }

    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    generateRandomWord();
  },[]);



  // const handleCharSelect = (char: string, index: any) => {
  //   const updatedPinCodes = [...pinCodes];
  //   updatedPinCodes[index] = char;
  //   setPinCodes(updatedPinCodes);
  // };


  return (    

    <Container  >
    <>
    {
      loggedInUser  
      ?<>
        <div className="game-container">

        <div className={styleUtils.title}><h3> Hangman </h3></div>
       <h5 className={styleUtils.center}> Guess the word - Enter a letter </h5>

       < Figure />
       <Word selectedWord={randomWord} correctLetters={correctLetters}/>

      {/* <div className={styleUtils.pinCodeInput}>

{pinCodes?.map((pinCode,index) => (
   <input
    key={index}
    type="text"
    className={styleUtils.pinInput}
    maxLength={1}
    value={pinCode} 
    disabled={true} 

  />
))}

      </div> */}


      <div className={styleUtils.title}><h6> Guesses left : </h6></div>


        </div>


 {/* <Alphabet onCharSelect={(char: string,index?:number) => handleCharSelect(char,index)} /> */}
      </> 
      : <GamePageLoggedOutView /> 
    }
    </>
    </Container>
  )
}
