import React, { useEffect, useState, } from 'react'
import { Button, Container } from 'react-bootstrap'
import { useParams } from 'react-router-dom';
import GamePageLoggedOutView from '../components/GamePageLoggedOutView'
import { HomePageProps } from '../components/types'
import styleUtils from '../styles/utils.module.css';

interface RouteParams {
  wordLength: string;
}

export default function GamePage({loggedInUser}:HomePageProps) {



  const { wordLength } = useParams() as unknown as RouteParams;
  const wordLengthVal = parseInt(wordLength);
  const [randomWord, setRandomWord] = useState('');
  const [pinCodes, setPinCodes] = useState(Array(wordLengthVal).fill(''));
  const [msg, setMsg] = useState("");


  const API_URL = `https://api.datamuse.com/words?sp=${wordLengthVal}&max=1000`;


  useEffect(() => {
    const token = localStorage.getItem("token");
    if(!!token){
      fetchLoggedInUser();
    }

  },[]);
  // const generateRandomWord = async () => {
  //   try {
  //     const response = await fetch(API_URL);
  //     if (response.ok) {
  //       const data = await response.json();
  //       setRandomWord(data.word);
  //       console.log(data);
  //     } else {
  //       throw new Error('Failed to fetch random word');
  //     }
  //   } catch (error) {
  //     console.error(error);
  //   }
  // };
  // const generateRandomWord = () => {
  //   const alphabet = 'abcdefghijklmnopqrstuvwxyz';
  //   let generatedWord = '';
  //   for (let i = 0; i < wordLengthVal; i++) {
  //     const randomIndex = Math.floor(Math.random() * alphabet.length);
  //     generatedWord += alphabet[randomIndex];
  //   }
  //   setRandomWord(generatedWord);
  // };

  const handlePinCodeChange = ({index, event}:any) => {

    const newPinCodes = [...pinCodes];
    newPinCodes[index] = event.target.value;
    setPinCodes(newPinCodes);

    if (index < wordLengthVal  - 1) {
      event.target.nextSibling.focus();
    }

    if(newPinCodes[index] === randomWord[index]){
      setMsg('right!');
    }else{
      setMsg('wrong!');
    }


  };

  return (    

    <Container >
    <>
    {
      loggedInUser  
      ?<>
       <div className={styleUtils.title}><h4> Please type your guesses :</h4></div>
      <div className={styleUtils.pinCodeInput}>

        {pinCodes.map((pinCode, index) => (
           <input
            key={index}
            type="text"
            className={styleUtils.pinInput}
            maxLength={1}
            value={pinCode}
            onChange={(event) => handlePinCodeChange({index, event})}
            tabIndex={index + 1} // Set the tabIndex to enable focusing with keyboard navigation
            autoFocus={index === 0} // Set autoFocus for the first input box

          />
        ))}
      </div>
      <h5 className={styleUtils.center}>Your guesses : {pinCodes} </h5>
      <h5 className={styleUtils.center}>Your Progress : {msg} </h5>
      <h5 className={styleUtils.center}>Random word : {randomWord} </h5>

      <div className={styleUtils.generateButton}>
      {/* <Button 
      className={styleUtils.buttonStyle} 
       onClick={generateRandomWord}
       > Generate
      </Button> */}
      </div>

      </> 
      : <GamePageLoggedOutView /> 
    }
    </>
    </Container>
  )
}
