import React from 'react';
import {styleUtils} from '../styles/u'

type AlphabetProps = {
  onCharSelect: (char: string) => void;
};

const Alphabet: React.FC<AlphabetProps> = ({ onCharSelect }) => {
  const alphabet = Array.from(Array(26), (_, i) => String.fromCharCode(65 + i)); // Generate an array of alphabet characters

  const handleCharSelect = (char: string) => {
    onCharSelect(char);
  };

  return (
    <div className={styleUtils.alphabet}>
      {alphabet.map((char) => (
        <button
          key={char}
          className={styleUtils.alphabetButton}
          onClick={() => handleCharSelect(char)}
        >
          {char}
        </button>
      ))}
    </div>
  );
};

export default Alphabet;
